create function [dbo].[HR_FUNC_EEOClass] (@iIntEnum integer, @EEOClassPref integer) returns varchar(100) as  begin  declare @oVarcharValuestring varchar(100) set @oVarcharValuestring = case  when @iIntEnum = 1 then  case when @EEOClassPref = 0 then 'First/Mid-Level Officials and MGRs' when @EEOClassPref = 1 then 'Officials and Administrators' else ''  end  when @iIntEnum = 2 then 'Professionals' when @iIntEnum = 3 then 'Technicians' when @iIntEnum = 4 then  case when @EEOClassPref = 0 then 'Sales Workers' when @EEOClassPref = 1 then 'Protective Service Workers' else ''  end  when @iIntEnum = 5 then  case when @EEOClassPref = 0 then 'Administrative Support Workers' when @EEOClassPref = 1 then 'Paraprofessionals' else ''  end  when @iIntEnum = 6 then  case when @EEOClassPref = 0 then 'Craft Workers' when @EEOClassPref = 1 then 'Administrative Support (Including Clerical and Sales)' else ''  end  when @iIntEnum = 7 then  case when @EEOClassPref = 0 then 'Operatives' when @EEOClassPref = 1 then 'Skilled Craft Workers' else ''  end  when @iIntEnum = 8 then  case when @EEOClassPref = 0 then 'Laborers and Helpers' when @EEOClassPref = 1 then 'Service Maintenance' else ''  end  when @iIntEnum = 9 then  case when @EEOClassPref = 0 then 'Service Workers' else ''  end  when @iIntEnum = 10 then  case when @EEOClassPref = 0 then 'Ex/SR- Level officials and MGRs' else ''  end  else ''  end  RETURN(@oVarcharValuestring)  END  
GO
