 CREATE     FUNCTION [dbo].[aagGetTotalBalance]() RETURNS  NUMERIC(19,5) AS BEGIN DECLARE @aaBudgetID INTEGER  , @aaCodeSequence INTEGER  , @ACTINDX INTEGER  , @aaActualPriliminary INTEGER  , @NETCHNG TINYINT  , @YEAR1 SMALLINT , @aaRange SMALLINT , @STR30 CHAR(31) , @aaFiscalPeriod INTEGER  ,  @aaTotal NUMERIC(19,5) , @USERID   CHAR(15) ,  @WINTYPE   SMALLINT , @CMPANYID INTEGER  SET @USERID = SYSTEM_USER  SET @WINTYPE  = 1 SET @CMPANYID = dbo.aagGetCompanyID()  SELECT @aaBudgetID = aaBudgetID , @aaCodeSequence = aaCodeSequence , @ACTINDX = ACTINDX , @aaActualPriliminary = aaActualPriliminary , @YEAR1 = AAG00906.YEAR1 , @aaRange = aaRange, @NETCHNG = NETCHNG, @STR30 = STR30 FROM AAG00906 WHERE USERID = @USERID AND WINTYPE = @WINTYPE AND  CMPANYID = @CMPANYID IF @NETCHNG = 0  BEGIN SELECT @aaTotal = PERDBLNC from AAG00903V  END ELSE BEGIN SELECT @aaTotal = SUM(PERDBLNC) from AAG00903V END RETURN (@aaTotal)  END    
GO
GRANT EXECUTE ON  [dbo].[aagGetTotalBalance] TO [DYNGRP]
GO
