 CREATE FUNCTION [dbo].[svcContractSiteInvoiceAmount]   (  @I_vCONTNBR char(11),  @I_vBillCust char(15),  @I_vBillAddress char(15),  @I_vCHECKDATE datetime,  @I_vIncludeMeter smallint,  @I_vFuncOrOrig tinyint = 0   ) RETURNS numeric(19,5) as BEGIN declare @iAmountToInvoice numeric(19,5),  @iOrigAmountToInvoice numeric(19,5),  @iMeterAmount numeric(19,5),  @iOrigMeterAmount numeric(19,5),  @MeterInvoiceDate datetime,  @iReturnValue numeric(19,5)   select @iAmountToInvoice = 0,  @iOrigAmountToInvoice = 0,  @iMeterAmount = 0,  @iOrigMeterAmount = 0,  @MeterInvoiceDate = convert(char(10), '1900-01-01', 101),  @iReturnValue = 0   if (@I_vCONTNBR <> '')  begin  select @iAmountToInvoice = sum(DOCAMNT) - sum(SVC_Invoice_Credit_Amoun) - sum(SVC00603.DSCDLRAM),  @iOrigAmountToInvoice = sum(ORDOCAMT) - sum(OrigInvCreditAmt) -sum(SVC00603.ORDDLRAT)  from SVC00603 WITH (NOLOCK)   where SVC00603.CONSTS = 2 and SVC00603.CONTNBR = @I_vCONTNBR AND SVC00603.INVODATE <= @I_vCHECKDATE and SVC00603.POSTED = 0  and SVC00603.LNSEQNBR in (select SVC00601.LNSEQNBR from SVC00601 where SVC00601.CONSTS = 2 and SVC00601.CONTNBR = @I_vCONTNBR  AND SVC00601.Bill_To_Customer = @I_vBillCust AND SVC00601.SVC_Bill_To_Address_Code = @I_vBillAddress and SVC00601.Credit_Hold =0)  select @iAmountToInvoice = isnull(@iAmountToInvoice,0)  select @iOrigAmountToInvoice = isnull(@iOrigAmountToInvoice,0)   if @I_vIncludeMeter =1  BEGIN  select @iMeterAmount = sum(SVC00607.Amount_To_Invoice),  @iOrigMeterAmount = sum(SVC00607.Orig_Amount_To_Invoice),  @MeterInvoiceDate = min(INVODATE)  from SVC00607 left join SVC00601  ON SVC00601.CONSTS = SVC00607.CONSTS AND SVC00601.CONTNBR = SVC00607.CONTNBR AND SVC00601.LNSEQNBR=SVC00607.LNSEQNBR  where SVC00607.CONSTS = 2 and SVC00607.CONTNBR = @I_vCONTNBR and SVC00607.Amount_To_Invoice > 0.0 and  INVODATE <= @I_vCHECKDATE and INVODATE > '1900-01-01'  AND SVC00601.Bill_To_Customer = @I_vBillCust AND SVC00601.SVC_Bill_To_Address_Code = @I_vBillAddress and SVC00601.Credit_Hold =0   select @iAmountToInvoice = isnull(@iMeterAmount,0) + isnull(@iAmountToInvoice,0)  select @iOrigAmountToInvoice = isnull(@iOrigMeterAmount,0) + isnull(@iOrigAmountToInvoice,0)  END  end  if (@I_vFuncOrOrig = 0)  set @iReturnValue = @iAmountToInvoice  else  set @iReturnValue = @iOrigAmountToInvoice   RETURN (@iReturnValue) END   
GO
GRANT EXECUTE ON  [dbo].[svcContractSiteInvoiceAmount] TO [DYNGRP]
GO
