 CREATE FUNCTION [dbo].[wfGetDocumentDrillDown]( @WfBusObjKey char(201), @Workflow_Type_Name char(51)) RETURNS varchar(256) AS BEGIN  declare @Table TABLE ( [Value] VARCHAR(201), [Cnt] int )  declare @Delimeter char(1)  declare @Value    VARCHAR(201)  declare @Index int  declare @Key1 varchar(100)  declare @Key2 varchar(100)  declare @Key3 varchar(100)  declare @Key4 varchar(100)  declare @DrillBack_Base_Url varchar(255)  declare @POSTATUS int  declare @VENDORID varchar(15)  declare @DrillBack varchar(255)   select @DrillBack = ''  select @Delimeter = '~'  select @Index = 1  WHILE LEN(@WfBusObjKey) > 0  BEGIN  SET @Value    = LEFT(@WfBusObjKey,  ISNULL(NULLIF(CHARINDEX(@Delimeter, @WfBusObjKey) - 1, -1),  LEN(@WfBusObjKey)))  SET @WfBusObjKey = SUBSTRING(@WfBusObjKey,  ISNULL(NULLIF(CHARINDEX(@Delimeter, @WfBusObjKey), 0),  LEN(@WfBusObjKey)) + 1, LEN(@WfBusObjKey))  Set @Value = LTRIM(RTRIM(@Value))  INSERT INTO @Table ( [Value], [Cnt] )  VALUES ( @Value, @Index )  select @Index = @Index + 1  END   select @DrillBack_Base_Url = DYNAMICS.dbo.createBaseDrillBackUrlString(DB_NAME(),0)  if (@Workflow_Type_Name = 'Purchase Order Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @POSTATUS = POSTATUS, @VENDORID = VENDORID from POP10100 where PONUMBER = @Key1  select @DrillBack = @DrillBack_Base_Url + dbo.dgppPurchaseOrder(1,1,1,0,@POSTATUS,@VENDORID,@Key1,'''','''','''','''','''' )  end  if (@Workflow_Type_Name = 'Purchase Requisition Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @DrillBack = @DrillBack_Base_Url + dbo.dgppPurchaseRequisitionNumber(1, @Key1, 0)  end   if (@Workflow_Type_Name = 'Payroll Timecard Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @Key2 =Value from @Table where Cnt = 2  select @Key3 =Value from @Table where Cnt = 3  select @Key4 =Value from @Table where Cnt = 4  select @DrillBack = @DrillBack_Base_Url + dbo.dgppUPRTimecard(1, @Key1, @Key2, @Key3, @Key4,0)  end  if (@Workflow_Type_Name = 'General Ledger Batch Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @Key2 =Value from @Table where Cnt = 2  select @Key3 =Value from @Table where Cnt = 3  select @Key4 =Value from @Table where Cnt = 4  select @DrillBack = @DrillBack_Base_Url + dbo.dgppGLBatchEntry(1, @Key1, @Key2, @Key3, @Key4)  end  if (@Workflow_Type_Name = 'Payables Batch Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @Key2 =Value from @Table where Cnt = 2  select @Key3 =Value from @Table where Cnt = 3  select @Key4 =Value from @Table where Cnt = 4  select @DrillBack = @DrillBack_Base_Url + dbo.dgppPMBatchEntry(1, @Key1, @Key2, @Key3, @Key4)  end  if (@Workflow_Type_Name = 'Receivables Batch Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @Key2 =Value from @Table where Cnt = 2  select @Key3 =Value from @Table where Cnt = 3  select @Key4 =Value from @Table where Cnt = 4  select @DrillBack = @DrillBack_Base_Url + dbo.dgppRMBatchEntry(1, @Key1, @Key2, @Key3, @Key4)  end   if (@Workflow_Type_Name = 'Timesheet Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @DrillBack = @DrillBack_Base_Url + dbo.dgppPTETimesheet(1, @Key1, 0)  end  if (@Workflow_Type_Name = 'Expense Report Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @DrillBack = @DrillBack_Base_Url + dbo.dgppPTEExpenseDocument(1, @Key1, 0)  end  if (@Workflow_Type_Name = 'Vendor Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @DrillBack = @DrillBack_Base_Url + dbo.dgppVendorID(1, @Key1)  end  if (@Workflow_Type_Name = 'Employee Profile Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @Key2 =Value from @Table where Cnt = 2  select @Key3 =Value from @Table where Cnt = 3  select @DrillBack = @DrillBack_Base_Url + dbo.dgppUPRESSEmpProfile(1, @Key1, @Key2, @Key3)  end  if (@Workflow_Type_Name = 'Payroll W4 Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @DrillBack = @DrillBack_Base_Url + dbo.dgppUPRESSW4(1, @Key1)  end   if (@Workflow_Type_Name = 'Payroll Direct Deposit Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @DrillBack = @DrillBack_Base_Url + dbo.dgppUPRESSDirectDeposit(1, @Key1)  end  if (@Workflow_Type_Name = 'Employee Skills Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @DrillBack = @DrillBack_Base_Url + dbo.dgppUPRESSSkillsTraining(1, @Key1)  end  if (@Workflow_Type_Name = 'Smartlist Designer View Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @DrillBack = @DrillBack_Base_Url + dbo.dgppSmartListView(1,@Key1)  end  if (@Workflow_Type_Name = 'Payables Transaction Approval')  Begin  select @Key1 =Value from @Table where Cnt = 1  select @DrillBack = @DrillBack_Base_Url + dbo.dgppOpenPMTrxNmbr(1, @Key1)  end  return @DrillBack END   
GO
GRANT EXECUTE ON  [dbo].[wfGetDocumentDrillDown] TO [DYNGRP]
