 create procedure [dbo].[SVC_Get_HTML_HistoryCall_Data]  (@srvrectype integer = NULL,  @callnbr char(11) = NULL) as  SELECT SVC30200.CALLNBR,SVC30200.SVCDESCR, SVC30200.ENTDTE, IV00101.ITEMDESC,   SVC00907.DSCRIPTN PROBDESCR,SVC00901.DSCRIPTN CAUSEDESCR,SVC00908.DSCRIPTN REPAIRDESCR, SY03900.TXTFIELD  FROM SVC30200 LEFT OUTER JOIN  SY03900 ON (SVC30200.NOTEINDX = SY03900.NOTEINDX) LEFT OUTER JOIN   SVC30201 ON (SVC30200.SRVRECTYPE = SVC30201.SRVRECTYPE and SVC30200.CALLNBR = SVC30201.CALLNBR) LEFT OUTER JOIN   SVC00907 ON (SVC30201.PROBCDE = SVC00907.PROBCDE) LEFT OUTER JOIN   SVC00901 ON (SVC30201.CAUSECDE = SVC00901.CAUSECDE) LEFT OUTER JOIN  SVC00908 ON (SVC30201.RPRCODE = SVC00908.RPRCODE) LEFT OUTER JOIN   IV00101 ON  (SVC30201.ITEMNMBR = IV00101.ITEMNMBR)  where SVC30200.SRVRECTYPE = @srvrectype and   SVC30200.CALLNBR = @callnbr and   SVC30200.Print_to_Web = 1    
