 CREATE Procedure [dbo].[SVC_ISC_Return_Address_RMAS]  (@custnmbr varchar(15) = NULL,  @ADRSCODE char(15))  as SELECT   SVC05200.RETDOCID 'RMA',   SVC05200.LNSEQNBR,  SVC05200.Return_Record_Type,   SVC05200.RMA_Status,  SVC05200.RETSTAT,  SVC05500.STSDESCR 'Status',  SVC05200.RETTYPE ,  SVC05501.DSCRIPTN 'Type',  SVC05200.ADRSCODE,  SVC05200.ADDRESS1,  SVC05200.ADDRESS2,  RTRIM(SVC05200.CITY) + ', ' + RTRIM(SVC05200.STATE) + '   ' + RTRIM(SVC05200.ZIPCODE) 'FULLCITY',   SVC05200.CONTACT 'Contact',  CONVERT(char(10),SVC05200.ENTDTE,101) + ' ' + CONVERT(char(5), SVC05200.ENTTME,108) 'Entered',  CONVERT(char(10),SVC05200.ETADTE,101) + ' ' + CONVERT(char(5), SVC05200.ETATME,108) 'ETA',  SVC05200.ITEMDESC 'Item',   CASE WHEN SVC05200.Received = 0 THEN 'False' ELSE 'True' END 'Received',   SVC05200.CUSTNMBR,   RM00101.CUSTNAME,   CONVERT(char(10),SVC05200.COMPDTE,101) + ' ' + CONVERT(char(5), SVC05200.COMPTME,108) 'COMPLETED',  SVC05200.RETREF,  SVC05200.ADDRESS3 FROM SVC05200 LEFT OUTER JOIN  SVC05500 ON (SVC05200.RETSTAT = SVC05500.RETSTAT) LEFT OUTER JOIN  SVC05501 ON (SVC05200.RETTYPE = SVC05501.RETTYPE) LEFT OUTER JOIN  RM00101 ON (SVC05200.CUSTNMBR = RM00101.CUSTNMBR) LEFT OUTER JOIN   SY03900 ON (SVC05200.NOTEINDX = SY03900.NOTEINDX)  where  SVC05200.CUSTNMBR = RTrim(@custnmbr) and  SVC05200.ADRSCODE = RTrim(@ADRSCODE) order by SVC05200.RETDOCID, SVC05200.LNSEQNBR  select * from SVC05255    
