 CREATE procedure [dbo].[aagValidateCodeGLWorkDistForAdjustment] @I_nValidationLevel smallint =0,  @I_cTableAssign nvarchar(30) = null, @I_cTableCode nvarchar(30) = null, @I_cTableError varchar(30) = null, @I_nHdrID int = 0, @I_nDistID    int = 0, @I_nDistDebitAmt numeric(19,5) = 0, @I_nDistCreditAmt numeric(19,5) = 0, @I_nAcctClassID int = 0, @I_nActIndx int = 0, @I_fDocVal tinyint = 1, @O_fSuccess tinyint = 1 output, @I_UserID varchar(20), @I_Right int  as begin  declare  @nAssignID    int,  @cTrxDim   varchar(15),  @cTrxDimCode   varchar(30),  @cRelTrxDim   varchar(15),  @cRelTrxDimCode  varchar(30),  @nErrorNum       int,  @nAssignDebitAmt  numeric(19,5),  @nAssignCreditAmt  numeric(19,5),  @nTotAssignDebitAmt numeric(19,5),  @nTotAssignCreditAmt numeric(19,5),  @fValidCodeSetting  tinyint,  @fValidCodeComb  tinyint,  @DisplayDistID int,  @Msg         varchar(500),  @cSourcDoc   varchar(15)   set @nAssignDebitAmt = 0  set @nAssignCreditAmt = 0  set @nTotAssignDebitAmt = 0  set @nTotAssignCreditAmt = 0  set @O_fSuccess = 1   Select @cSourcDoc = SOURCDOC from AAG30001 where aaGLHdrID in (  Select aaGLHdrID from AAG30000 where JRNENTRY in (Select JRNENTRY from AAG10000 where aaGLWorkHdrID =  @I_nHdrID))  if @cSourcDoc <> ''  begin  return  end  exec ('delete from ' + @I_cTableError + ' where aaGLWorkHdrID = ' + @I_nHdrID +  ' and aaGLWorkDistID =' + @I_nDistID)  exec ('declare aaGLWorkDist cursor fast_forward FOR select aaGLWorkAssignID,DEBITAMT,CRDTAMNT  from ' + @I_cTableAssign + ' where aaGLWorkHdrID = ' + @I_nHdrID +  ' and aaGLWorkDistID =' + @I_nDistID)  open aaGLWorkDist  fetch next from aaGLWorkDist into @nAssignID,@nAssignDebitAmt,@nAssignCreditAmt  WHILE @@fetch_status = 0  begin  exec aagCheckCodeSettingAssignGLWorkForAdjustment  @I_nValidationLevel,  @I_cTableCode,  @I_cTableError,  @I_nHdrID,  @I_nDistID,  @I_nAcctClassID,  @I_nActIndx,  @nAssignID,  @O_fSuccess,  @fValidCodeSetting output,  @I_UserID,  @I_Right   if @fValidCodeSetting <> 1  set @O_fSuccess = 0   exec aagValidateCodeGLWorkAssign  @I_cTableCode,  @I_nHdrID,  @I_nDistID,  @I_nAcctClassID,  @nAssignID,  @cTrxDim output,  @cTrxDimCode output,  @cRelTrxDim output,  @cRelTrxDimCode output,  @fValidCodeComb output  if @fValidCodeComb = 0  begin  set @O_fSuccess = 0  set @nErrorNum = 115   select @Msg='insert ' + @I_cTableError + '(aaGLWorkHdrID, aaGLWorkDistID,aaGLWorkAssignID,'+  'aaTrxDim,aaTrxDimCode,aaRelatedTrxDim,aaRelatedTrxDimCode,aaErrorNum)'+  ' values(' + convert(varchar,@I_nHdrID) + ',' + convert(varchar,@I_nDistID) + ','  + convert(varchar,@nAssignID)  + ','''+ @cTrxDim  + ''','''+ @cTrxDimCode  + ''','''+ @cRelTrxDim  + ''','''+ @cRelTrxDimCode  + ''',' + convert(varchar,@nErrorNum)  + ')'  exec (@Msg)   end   set @nTotAssignDebitAmt = @nTotAssignDebitAmt + @nAssignDebitAmt  set @nTotAssignCreditAmt = @nTotAssignCreditAmt + @nAssignCreditAmt  fetch next from aaGLWorkDist into @nAssignID,@nAssignDebitAmt,@nAssignCreditAmt  end  close aaGLWorkDist  deallocate aaGLWorkDist  if(select aaIsOptionSet from AAG00700 where aaOption=6)=1  begin  if @I_nValidationLevel <> 1 and (@nTotAssignDebitAmt <> @I_nDistDebitAmt or @nTotAssignCreditAmt <> @I_nDistCreditAmt)  begin  if (@nTotAssignDebitAmt > @I_nDistDebitAmt or @nTotAssignCreditAmt > @I_nDistCreditAmt)  set @nErrorNum = 112   else  set @nErrorNum = 101   set @nAssignID = 0  exec ('insert ' + @I_cTableError + '(aaGLWorkHdrID, aaGLWorkDistID,aaGLWorkAssignID,aaErrorNum)  values(' + @I_nHdrID + ',' + @I_nDistID + ',' + @nAssignID + ','+ @nErrorNum + ')')   set @O_fSuccess = 0  end  end  else  if @I_nValidationLevel <> 1 and (@nTotAssignDebitAmt <> @I_nDistDebitAmt or @nTotAssignCreditAmt <> @I_nDistCreditAmt)  begin  if (@nTotAssignDebitAmt > @I_nDistDebitAmt or @nTotAssignCreditAmt > @I_nDistCreditAmt)  begin  set @nErrorNum = 112   set @nAssignID = 0   exec ('insert ' + @I_cTableError + '(aaGLWorkHdrID, aaGLWorkDistID,aaGLWorkAssignID,aaErrorNum)  values(' + @I_nHdrID + ',' + @I_nDistID + ',' + @nAssignID + ','+ @nErrorNum + ')')   set @O_fSuccess = 0  end  end  if @I_fDocVal = 1  exec('update ' + @I_cTableError + ' set AADOCVAL = 1 where aaGLWorkHdrID = ' + @I_nHdrID +  ' and AADOCVAL = 0')  exec aagGetDisplayDistIDGLWork @I_nHdrID, @I_nDistID, 0, @DisplayDistID out  exec ('update ' + @I_cTableError + ' set aaDisplayDistID = ' + @DisplayDistID + ' where aaGLWorkHdrID = ' + @I_nHdrID +  ' and aaGLWorkDistID = ' + @I_nDistID)  return end    
GO
GRANT EXECUTE ON  [dbo].[aagValidateCodeGLWorkDistForAdjustment] TO [DYNGRP]
