 CREATE PROC [dbo].[zDP_DD10700L_2] (@EMPLOYID_RS char(15), @INDXLONG_RS int, @EMPLOYID_RE char(15), @INDXLONG_RE int) AS  set nocount on IF @EMPLOYID_RS IS NULL BEGIN SELECT TOP 25  INDXLONG, EMPLOYID, DDTRANUM, DDACTNUM, DDTRANS, DDAMTDLR, AMNTOPST, DDITEM, ERRDESCR, USERID, DEX_ROW_ID FROM .DD10700 ORDER BY EMPLOYID DESC, INDXLONG DESC END ELSE IF @EMPLOYID_RS = @EMPLOYID_RE BEGIN SELECT TOP 25  INDXLONG, EMPLOYID, DDTRANUM, DDACTNUM, DDTRANS, DDAMTDLR, AMNTOPST, DDITEM, ERRDESCR, USERID, DEX_ROW_ID FROM .DD10700 WHERE EMPLOYID = @EMPLOYID_RS AND INDXLONG BETWEEN @INDXLONG_RS AND @INDXLONG_RE ORDER BY EMPLOYID DESC, INDXLONG DESC END ELSE BEGIN SELECT TOP 25  INDXLONG, EMPLOYID, DDTRANUM, DDACTNUM, DDTRANS, DDAMTDLR, AMNTOPST, DDITEM, ERRDESCR, USERID, DEX_ROW_ID FROM .DD10700 WHERE EMPLOYID BETWEEN @EMPLOYID_RS AND @EMPLOYID_RE AND INDXLONG BETWEEN @INDXLONG_RS AND @INDXLONG_RE ORDER BY EMPLOYID DESC, INDXLONG DESC END set nocount off    
GO
GRANT EXECUTE ON  [dbo].[zDP_DD10700L_2] TO [DYNGRP]
GO
