 CREATE PROC [dbo].[zDP_GL40100N_2] (@BS int, @OFFINDX int, @DEX_ROW_ID int, @OFFINDX_RS int, @OFFINDX_RE int) AS  set nocount on IF @OFFINDX_RS IS NULL BEGIN SELECT TOP 25  BSNSFMID, BSNSFDSC, OFFINDX, OVOACNUM, BCHEMSG1, BCHEMSG2, SOURCDOC, REFRENCE, BRKDNALL, NOTEINDX, DEX_ROW_ID FROM .GL40100 WHERE ( OFFINDX = @OFFINDX AND DEX_ROW_ID > @DEX_ROW_ID OR OFFINDX > @OFFINDX ) ORDER BY OFFINDX ASC, DEX_ROW_ID ASC END ELSE IF @OFFINDX_RS = @OFFINDX_RE BEGIN SELECT TOP 25  BSNSFMID, BSNSFDSC, OFFINDX, OVOACNUM, BCHEMSG1, BCHEMSG2, SOURCDOC, REFRENCE, BRKDNALL, NOTEINDX, DEX_ROW_ID FROM .GL40100 WHERE OFFINDX = @OFFINDX_RS AND ( OFFINDX = @OFFINDX AND DEX_ROW_ID > @DEX_ROW_ID OR OFFINDX > @OFFINDX ) ORDER BY OFFINDX ASC, DEX_ROW_ID ASC END ELSE BEGIN SELECT TOP 25  BSNSFMID, BSNSFDSC, OFFINDX, OVOACNUM, BCHEMSG1, BCHEMSG2, SOURCDOC, REFRENCE, BRKDNALL, NOTEINDX, DEX_ROW_ID FROM .GL40100 WHERE OFFINDX BETWEEN @OFFINDX_RS AND @OFFINDX_RE AND ( OFFINDX = @OFFINDX AND DEX_ROW_ID > @DEX_ROW_ID OR OFFINDX > @OFFINDX ) ORDER BY OFFINDX ASC, DEX_ROW_ID ASC END set nocount off    
GO
