 CREATE PROC [dbo].[zDP_GL40200N_1] (@BS int, @SGMTNUMB smallint, @SGMNTID char(67), @SGMTNUMB_RS smallint, @SGMNTID_RS char(67), @SGMTNUMB_RE smallint, @SGMNTID_RE char(67)) AS  set nocount on IF @SGMTNUMB_RS IS NULL BEGIN SELECT TOP 25  SGMTNUMB, SGMNTID, DSCRIPTN, SEGCOUNT, NOTEINDX, DEX_ROW_TS, DEX_ROW_ID FROM .GL40200 WHERE ( SGMTNUMB = @SGMTNUMB AND SGMNTID > @SGMNTID OR SGMTNUMB > @SGMTNUMB ) ORDER BY SGMTNUMB ASC, SGMNTID ASC END ELSE IF @SGMTNUMB_RS = @SGMTNUMB_RE BEGIN SELECT TOP 25  SGMTNUMB, SGMNTID, DSCRIPTN, SEGCOUNT, NOTEINDX, DEX_ROW_TS, DEX_ROW_ID FROM .GL40200 WHERE SGMTNUMB = @SGMTNUMB_RS AND SGMNTID BETWEEN @SGMNTID_RS AND @SGMNTID_RE AND ( SGMTNUMB = @SGMTNUMB AND SGMNTID > @SGMNTID OR SGMTNUMB > @SGMTNUMB ) ORDER BY SGMTNUMB ASC, SGMNTID ASC END ELSE BEGIN SELECT TOP 25  SGMTNUMB, SGMNTID, DSCRIPTN, SEGCOUNT, NOTEINDX, DEX_ROW_TS, DEX_ROW_ID FROM .GL40200 WHERE SGMTNUMB BETWEEN @SGMTNUMB_RS AND @SGMTNUMB_RE AND SGMNTID BETWEEN @SGMNTID_RS AND @SGMNTID_RE AND ( SGMTNUMB = @SGMTNUMB AND SGMNTID > @SGMNTID OR SGMTNUMB > @SGMTNUMB ) ORDER BY SGMTNUMB ASC, SGMNTID ASC END set nocount off    
GO
GRANT EXECUTE ON  [dbo].[zDP_GL40200N_1] TO [DYNGRP]
