 CREATE PROC [dbo].[zDP_PP70300L_1] (@RPTGRIND_RS smallint, @RTPACHIN_RS smallint, @RTCFGWIN_RS smallint, @RTGRSBIN_RS numeric(19,5), @RPTGRIND_RE smallint, @RTPACHIN_RE smallint, @RTCFGWIN_RE smallint, @RTGRSBIN_RE numeric(19,5)) AS  set nocount on IF @RPTGRIND_RS IS NULL BEGIN SELECT TOP 25  RTGRSBIN, RTPACHIN, RPTGRIND, RPTGRNMS, RTCFGWIN, DEX_ROW_ID FROM .PP70300 ORDER BY RPTGRIND DESC, RTPACHIN DESC, RTCFGWIN DESC, RTGRSBIN DESC END ELSE IF @RPTGRIND_RS = @RPTGRIND_RE BEGIN SELECT TOP 25  RTGRSBIN, RTPACHIN, RPTGRIND, RPTGRNMS, RTCFGWIN, DEX_ROW_ID FROM .PP70300 WHERE RPTGRIND = @RPTGRIND_RS AND RTPACHIN BETWEEN @RTPACHIN_RS AND @RTPACHIN_RE AND RTCFGWIN BETWEEN @RTCFGWIN_RS AND @RTCFGWIN_RE AND RTGRSBIN BETWEEN @RTGRSBIN_RS AND @RTGRSBIN_RE ORDER BY RPTGRIND DESC, RTPACHIN DESC, RTCFGWIN DESC, RTGRSBIN DESC END ELSE BEGIN SELECT TOP 25  RTGRSBIN, RTPACHIN, RPTGRIND, RPTGRNMS, RTCFGWIN, DEX_ROW_ID FROM .PP70300 WHERE RPTGRIND BETWEEN @RPTGRIND_RS AND @RPTGRIND_RE AND RTPACHIN BETWEEN @RTPACHIN_RS AND @RTPACHIN_RE AND RTCFGWIN BETWEEN @RTCFGWIN_RS AND @RTCFGWIN_RE AND RTGRSBIN BETWEEN @RTGRSBIN_RS AND @RTGRSBIN_RE ORDER BY RPTGRIND DESC, RTPACHIN DESC, RTCFGWIN DESC, RTGRSBIN DESC END set nocount off    
