GO
SET ANSI_NULLS ON
GO
 CREATE PROC [dbo].[zDP_SOP00300N_1] (@BS int, @CUSTNMBR char(15), @ITEMNMBR char(31), @SEQNUMBR int, @CUSTNMBR_RS char(15), @ITEMNMBR_RS char(31), @SEQNUMBR_RS int, @CUSTNMBR_RE char(15), @ITEMNMBR_RE char(31), @SEQNUMBR_RE int) AS  set nocount on IF @CUSTNMBR_RS IS NULL BEGIN SELECT TOP 25  CUSTNMBR, ITEMNMBR, SEQNUMBR, SUBITEMNMBR, SUBITEMDESC, STRTDATE, ENDDATE, DEX_ROW_ID FROM .SOP00300 WHERE ( CUSTNMBR = @CUSTNMBR AND ITEMNMBR = @ITEMNMBR AND SEQNUMBR > @SEQNUMBR OR CUSTNMBR = @CUSTNMBR AND ITEMNMBR > @ITEMNMBR OR CUSTNMBR > @CUSTNMBR ) ORDER BY CUSTNMBR ASC, ITEMNMBR ASC, SEQNUMBR ASC END ELSE IF @CUSTNMBR_RS = @CUSTNMBR_RE BEGIN SELECT TOP 25  CUSTNMBR, ITEMNMBR, SEQNUMBR, SUBITEMNMBR, SUBITEMDESC, STRTDATE, ENDDATE, DEX_ROW_ID FROM .SOP00300 WHERE CUSTNMBR = @CUSTNMBR_RS AND ITEMNMBR BETWEEN @ITEMNMBR_RS AND @ITEMNMBR_RE AND SEQNUMBR BETWEEN @SEQNUMBR_RS AND @SEQNUMBR_RE AND ( CUSTNMBR = @CUSTNMBR AND ITEMNMBR = @ITEMNMBR AND SEQNUMBR > @SEQNUMBR OR CUSTNMBR = @CUSTNMBR AND ITEMNMBR > @ITEMNMBR OR CUSTNMBR > @CUSTNMBR ) ORDER BY CUSTNMBR ASC, ITEMNMBR ASC, SEQNUMBR ASC END ELSE BEGIN SELECT TOP 25  CUSTNMBR, ITEMNMBR, SEQNUMBR, SUBITEMNMBR, SUBITEMDESC, STRTDATE, ENDDATE, DEX_ROW_ID FROM .SOP00300 WHERE CUSTNMBR BETWEEN @CUSTNMBR_RS AND @CUSTNMBR_RE AND ITEMNMBR BETWEEN @ITEMNMBR_RS AND @ITEMNMBR_RE AND SEQNUMBR BETWEEN @SEQNUMBR_RS AND @SEQNUMBR_RE AND ( CUSTNMBR = @CUSTNMBR AND ITEMNMBR = @ITEMNMBR AND SEQNUMBR > @SEQNUMBR OR CUSTNMBR = @CUSTNMBR AND ITEMNMBR > @ITEMNMBR OR CUSTNMBR > @CUSTNMBR ) ORDER BY CUSTNMBR ASC, ITEMNMBR ASC, SEQNUMBR ASC END set nocount off    
GO
GRANT EXECUTE ON  [dbo].[zDP_SOP00300N_1] TO [DYNGRP]
GO
