 CREATE PROC [dbo].[zDP_SVC00960N_2] (@BS int, @LASTNAME char(21), @FRSTNAME char(15), @DEX_ROW_ID int, @LASTNAME_RS char(21), @FRSTNAME_RS char(15), @LASTNAME_RE char(21), @FRSTNAME_RE char(15)) AS  set nocount on IF @LASTNAME_RS IS NULL BEGIN SELECT TOP 25  CUSTNMBR, ADRSCODE, FRSTNAME, LASTNAME, PHONNAME, FAXNUMBR, ANI_Number, EMail, DEX_ROW_ID FROM .SVC00960 WHERE ( LASTNAME = @LASTNAME AND FRSTNAME = @FRSTNAME AND DEX_ROW_ID > @DEX_ROW_ID OR LASTNAME = @LASTNAME AND FRSTNAME > @FRSTNAME OR LASTNAME > @LASTNAME ) ORDER BY LASTNAME ASC, FRSTNAME ASC, DEX_ROW_ID ASC END ELSE IF @LASTNAME_RS = @LASTNAME_RE BEGIN SELECT TOP 25  CUSTNMBR, ADRSCODE, FRSTNAME, LASTNAME, PHONNAME, FAXNUMBR, ANI_Number, EMail, DEX_ROW_ID FROM .SVC00960 WHERE LASTNAME = @LASTNAME_RS AND FRSTNAME BETWEEN @FRSTNAME_RS AND @FRSTNAME_RE AND ( LASTNAME = @LASTNAME AND FRSTNAME = @FRSTNAME AND DEX_ROW_ID > @DEX_ROW_ID OR LASTNAME = @LASTNAME AND FRSTNAME > @FRSTNAME OR LASTNAME > @LASTNAME ) ORDER BY LASTNAME ASC, FRSTNAME ASC, DEX_ROW_ID ASC END ELSE BEGIN SELECT TOP 25  CUSTNMBR, ADRSCODE, FRSTNAME, LASTNAME, PHONNAME, FAXNUMBR, ANI_Number, EMail, DEX_ROW_ID FROM .SVC00960 WHERE LASTNAME BETWEEN @LASTNAME_RS AND @LASTNAME_RE AND FRSTNAME BETWEEN @FRSTNAME_RS AND @FRSTNAME_RE AND ( LASTNAME = @LASTNAME AND FRSTNAME = @FRSTNAME AND DEX_ROW_ID > @DEX_ROW_ID OR LASTNAME = @LASTNAME AND FRSTNAME > @FRSTNAME OR LASTNAME > @LASTNAME ) ORDER BY LASTNAME ASC, FRSTNAME ASC, DEX_ROW_ID ASC END set nocount off    
