 CREATE PROC [dbo].[zDP_SVC01101N_1] (@BS int, @SPLTTERMS char(11), @LNITMSEQ int, @SPLTTERMS_RS char(11), @LNITMSEQ_RS int, @SPLTTERMS_RE char(11), @LNITMSEQ_RE int) AS  set nocount on IF @SPLTTERMS_RS IS NULL BEGIN SELECT TOP 25  SPLTTERMS, LNITMSEQ, DLRORPCT, LISTPRCE, TYPEID, SRVSTAT, DEX_ROW_ID FROM .SVC01101 WHERE ( SPLTTERMS = @SPLTTERMS AND LNITMSEQ > @LNITMSEQ OR SPLTTERMS > @SPLTTERMS ) ORDER BY SPLTTERMS ASC, LNITMSEQ ASC END ELSE IF @SPLTTERMS_RS = @SPLTTERMS_RE BEGIN SELECT TOP 25  SPLTTERMS, LNITMSEQ, DLRORPCT, LISTPRCE, TYPEID, SRVSTAT, DEX_ROW_ID FROM .SVC01101 WHERE SPLTTERMS = @SPLTTERMS_RS AND LNITMSEQ BETWEEN @LNITMSEQ_RS AND @LNITMSEQ_RE AND ( SPLTTERMS = @SPLTTERMS AND LNITMSEQ > @LNITMSEQ OR SPLTTERMS > @SPLTTERMS ) ORDER BY SPLTTERMS ASC, LNITMSEQ ASC END ELSE BEGIN SELECT TOP 25  SPLTTERMS, LNITMSEQ, DLRORPCT, LISTPRCE, TYPEID, SRVSTAT, DEX_ROW_ID FROM .SVC01101 WHERE SPLTTERMS BETWEEN @SPLTTERMS_RS AND @SPLTTERMS_RE AND LNITMSEQ BETWEEN @LNITMSEQ_RS AND @LNITMSEQ_RE AND ( SPLTTERMS = @SPLTTERMS AND LNITMSEQ > @LNITMSEQ OR SPLTTERMS > @SPLTTERMS ) ORDER BY SPLTTERMS ASC, LNITMSEQ ASC END set nocount off    
GO
