 CREATE PROC [dbo].[zDP_UPR40300F_2] (@DSCRIPTN_RS char(31), @DEPRTMNT_RS char(7), @DSCRIPTN_RE char(31), @DEPRTMNT_RE char(7)) AS  set nocount on IF @DSCRIPTN_RS IS NULL BEGIN SELECT TOP 25  DEPRTMNT, DSCRIPTN, AddlDesc, NOTEINDX, CHANGEBY_I, CHANGEDATE_I, DEX_ROW_ID FROM .UPR40300 ORDER BY DSCRIPTN ASC, DEPRTMNT ASC END ELSE IF @DSCRIPTN_RS = @DSCRIPTN_RE BEGIN SELECT TOP 25  DEPRTMNT, DSCRIPTN, AddlDesc, NOTEINDX, CHANGEBY_I, CHANGEDATE_I, DEX_ROW_ID FROM .UPR40300 WHERE DSCRIPTN = @DSCRIPTN_RS AND DEPRTMNT BETWEEN @DEPRTMNT_RS AND @DEPRTMNT_RE ORDER BY DSCRIPTN ASC, DEPRTMNT ASC END ELSE BEGIN SELECT TOP 25  DEPRTMNT, DSCRIPTN, AddlDesc, NOTEINDX, CHANGEBY_I, CHANGEDATE_I, DEX_ROW_ID FROM .UPR40300 WHERE DSCRIPTN BETWEEN @DSCRIPTN_RS AND @DSCRIPTN_RE AND DEPRTMNT BETWEEN @DEPRTMNT_RS AND @DEPRTMNT_RE ORDER BY DSCRIPTN ASC, DEPRTMNT ASC END set nocount off    
GO
GRANT EXECUTE ON  [dbo].[zDP_UPR40300F_2] TO [DYNGRP]
