 CREATE PROC [dbo].[zDP_palbCashL_1] (@BACHNUMB_RS char(15), @DOCNUMBR_RS char(21), @BACHNUMB_RE char(15), @DOCNUMBR_RE char(21)) AS  set nocount on IF @BACHNUMB_RS IS NULL BEGIN SELECT TOP 25  BACHNUMB, BCHSOURC, RMDTYPAL, DOCNUMBR, CUSTNMBR, CUSTNAME, BNKBRNCH, BANKNAME, BNKACTNM, LockboxID, ERRSTATE, DEX_ROW_ID FROM .palbCash ORDER BY BACHNUMB DESC, DOCNUMBR DESC END ELSE IF @BACHNUMB_RS = @BACHNUMB_RE BEGIN SELECT TOP 25  BACHNUMB, BCHSOURC, RMDTYPAL, DOCNUMBR, CUSTNMBR, CUSTNAME, BNKBRNCH, BANKNAME, BNKACTNM, LockboxID, ERRSTATE, DEX_ROW_ID FROM .palbCash WHERE BACHNUMB = @BACHNUMB_RS AND DOCNUMBR BETWEEN @DOCNUMBR_RS AND @DOCNUMBR_RE ORDER BY BACHNUMB DESC, DOCNUMBR DESC END ELSE BEGIN SELECT TOP 25  BACHNUMB, BCHSOURC, RMDTYPAL, DOCNUMBR, CUSTNMBR, CUSTNAME, BNKBRNCH, BANKNAME, BNKACTNM, LockboxID, ERRSTATE, DEX_ROW_ID FROM .palbCash WHERE BACHNUMB BETWEEN @BACHNUMB_RS AND @BACHNUMB_RE AND DOCNUMBR BETWEEN @DOCNUMBR_RS AND @DOCNUMBR_RE ORDER BY BACHNUMB DESC, DOCNUMBR DESC END set nocount off    
GO
GRANT EXECUTE ON  [dbo].[zDP_palbCashL_1] TO [DYNGRP]
