 CREATE VIEW [dbo].[ASIEXP28] AS SELECT   A.POPRCTNM,  B.POPTYPE,  A.RCPTLNNM,  A.PONUMBER,  A.ITEMNMBR,  A.ITEMDESC,  A.Landed_Cost_Group_ID,  A.Landed_Cost,  A.RCPTRETNUM,  A.INVRETNUM,  A.VNDITDSC,  A.UMQTYINB,  A.ACTLSHIP,  A.COMMNTID,  A.INVINDX,  A.UOFM,  A.UNITCOST,  A.EXTDCOST,  A.LOCNCODE,  A.NONINVEN,  A.DECPLCUR,  A.DECPLQTY,  A.ITMTRKOP,  A.TRXSORCE,  A.JOBNUMBR,  A.COSTCODE,  A.COSTTYPE,  A.CURRNIDX,  A.ORUNTCST,  A.OREXTCST,  A.ODECPLCU,  A.BOLPRONUMBER,  A.Capital_Item,  A.Product_Indicator,  A.Purchase_IV_Item_Taxable,  A.Purchase_Item_Tax_Schedu,  A.Purchase_Site_Tax_Schedu,  A.BSIVCTTL,  A.TAXAMNT,  A.ORTAXAMT,  A.PURPVIDX,  0 as PSTGSTUS,  B.VNDDOCNM,  B.receiptdate,  B.GLPOSTDT,  B.BCHSOURC,  B.BACHNUMB,  B.VENDORID,  B.VENDNAME,  B.POSTEDDT,  B.PTDUSRID,  B.USER2ENT,  B.CREATDDT,  B.MODIFDT,  B.Tax_Date,  B.CURNCYID,  B.XCHGRATE,  B.RATECALC,  B.DENXRATE,  A.ProjNum,  A.CostCatID FROM POP10310 A INNER JOIN POP10300 B ON A.POPRCTNM = B.POPRCTNM  UNION ALL SELECT   A.POPRCTNM,  B.POPTYPE,  A.RCPTLNNM,  A.PONUMBER,  A.ITEMNMBR,  A.ITEMDESC,  A.Landed_Cost_Group_ID,  A.Landed_Cost,  A.RCPTRETNUM,  A.INVRETNUM,  A.VNDITDSC,  A.UMQTYINB,  A.ACTLSHIP,  A.COMMNTID,  A.INVINDX,  A.UOFM,  A.UNITCOST,  A.EXTDCOST,  A.LOCNCODE,  A.NONINVEN,  A.DECPLCUR,  A.DECPLQTY,  A.ITMTRKOP,  A.TRXSORCE,  A.JOBNUMBR,  A.COSTCODE,  A.COSTTYPE,  A.CURRNIDX,  A.ORUNTCST,  A.OREXTCST,  A.ODECPLCU,  A.BOLPRONUMBER,  A.Capital_Item,  A.Product_Indicator,  A.Purchase_IV_Item_Taxable,  A.Purchase_Item_Tax_Schedu,  A.Purchase_Site_Tax_Schedu,  A.BSIVCTTL,  A.TAXAMNT,  A.ORTAXAMT,  A.PURPVIDX,  1 as PSTGSTUS,  B.VNDDOCNM,  B.receiptdate,  B.GLPOSTDT,  B.BCHSOURC,  B.BACHNUMB,  B.VENDORID,  B.VENDNAME,  B.POSTEDDT,  B.PTDUSRID,  B.USER2ENT,  B.CREATDDT,  B.MODIFDT,  B.Tax_Date,  B.CURNCYID,  B.XCHGRATE,  B.RATECALC,  B.DENXRATE,  A.ProjNum,  A.CostCatID FROM POP30310 A INNER JOIN POP30300 B ON A.POPRCTNM = B.POPRCTNM   
