CSTATUS = 3,   0 as 'POSTATUS',  'Hist.' as 'STSDESCR',  ['PM Transaction HIST'].[DOCAMNT] as 'DOCAMNT',  (select 'CURNCYID' = case ['PM Key Master'].[CNTRLTYP]  when 2 then ''  else rtrim(['PM Transaction HIST'].[CURNCYID])  end),  '' as 'PRSTADCD',  ['PM Transaction HIST'].[VOIDED] as 'VOIDED',  '01-01-1900' as 'PRMDATE',  '01-01-1900' as 'REQDATE',  '01-01-1900' as Workflow_Due_Date,  0 as 'Workflow_Approval_Status',  '' as Workflow_Name,   '' as Workflow_Approver,  0 as Workflow_Priority,  '' as Workflow_Originator,  ['PM Transaction HIST'].[HOLD] as 'HOLD',  DUEDATE = ['PM Transaction HIST'].[DUEDATE],  DISCDATE = ['PM Transaction HIST'].[DISCDATE],  0 as WF_Step_Type,  ['PM Transaction HIST'].[BACHNUMB] as 'BACHNUMB',  ['PM Transaction HIST'].[BCHSOURC] as 'BCHSOURC',  'Recond' = dbo.cmIsReconciledDocument(['PM Transaction HIST'].DOCTYPE, ['PM Transaction HIST'].CHEKBKID,  ['PM Transaction HIST'].DOCNUMBR, ['PM Transaction HIST'].VENDORID, ['PM Transaction HIST'].TRXSORCE,  ['PM Transaction HIST'].VCHRNMBR),  'Emailed' = 0,['PM Transaction HIST'].[Workflow_Status] as 'Workflow_Status'   from [PM00400] as ['PM Key Master'] with (NOLOCK)  join [PM30200] as ['PM Transaction HIST'] with (NOLOCK)   on (['PM Key Master'].[DOCTYPE] = ['PM Transaction HIST'].[DOCTYPE] and   ['PM Key Master'].[CNTRLNUM] = ['PM Transaction HIST'].[VCHRNMBR])  left outer join [PM00200] as ['PM Vendor Master'] with (NOLOCK)  on (['PM Key Master'].[VENDORID] = ['PM Vendor Master'].[VENDORID])  where ['PM Key Master'].[DCSTATUS] = 3   )   
